import * as React from 'react';
import { SkillMap } from '../models';

interface Props {
  children: React.ReactNode;
}

interface IFilterContext {
  filtersMatches: Set<string> | null;
  handleFilter: (query: string) => void;
  addToSkillMap: (skillMap: SkillMap) => void;
}

const FilterContext = React.createContext<IFilterContext>({
  filtersMatches: null,
  handleFilter: () => null,
  addToSkillMap: () => null,
});

export function FilterProvider(props: Props) {
  const [skillMap, setSkillMap] = React.useState<SkillMap>({});
  const [filtersMatches, setMatches] = React.useState<Set<string> | null>(null);
  // keep the map, also keep track of the sorted keys. (if performance becomes a concern).

  function handleFilter(query: string) {
    if (query.trim() === '') {
      return setMatches(null);
    }

    const sanitizedQuery = query.toLowerCase();
    const skills = Object.keys(skillMap);
    const filteredSkills = skills.filter(key => key.includes(sanitizedQuery));
    const treeIds = new Set(filteredSkills.map(skill => skillMap[skill]));

    return setMatches(treeIds);
  }

  function addToSkillMap(skillMap: SkillMap) {
    return setSkillMap(prev => ({
      ...prev,
      ...skillMap,
    }));
  }

  return (
    <FilterContext.Provider
      value={{
        filtersMatches,
        handleFilter,
        addToSkillMap,
      }}
    >
      {props.children}
    </FilterContext.Provider>
  );
}

export default FilterContext;
import * as React from 'react';
import AppContext from '../context/AppContext';
import styled, { ThemeProvider } from 'styled-components';
import defaultTheme from '../theme/index';
import { DeepPartial } from '../models/utils';
import { SkillGroupData } from '../models';
import FilterContext from '../context/FilterContext';

type Props = {
  children: (treeData: SkillGroupData) => React.ReactNode;
} & DeepPartial<typeof defaultProps>;

const defaultProps = {
  theme: defaultTheme,
};

function SkillTreeGroup({ theme, children }: Props) {
  const { skillCount, selectedSkillCount, resetSkills } = React.useContext(
    AppContext
  );

  const { handleFilter } = React.useContext(FilterContext);
  const skillTreeTheme = React.useMemo(() => ({ ...defaultTheme, ...theme }), [
    theme,
  ]);

  const treeData: SkillGroupData = {
    skillCount,
    selectedSkillCount,
    resetSkills,
    handleFilter,
  };

  return (
    <ThemeProvider theme={skillTreeTheme}>
      <StyleSkillTreeGroup>{children(treeData)}</StyleSkillTreeGroup>
    </ThemeProvider>
  );
}

SkillTreeGroup.defaultProps = defaultProps;

export default SkillTreeGroup;

const StyleSkillTreeGroup = styled.div`
