import * as React from 'react';
import { mapValues } from 'lodash';
import {
  NodeState,
  ContextStorage,
  Action,
  SavedDataType,
  SkillData,
  NodeSelectEvent,
} from '../models';
import AppContext, { IAppContext } from './AppContext';
import { SELECTED_STATE, LOCKED_STATE } from '../components/constants';

type Props = typeof SkillTreeProvider.defaultProps & {
  treeId: string;
  savedData?: SavedDataType;
  storage?: ContextStorage;
};

type DefaultProps = {
  handleSave: (
    storage: ContextStorage,
    id: string,
    skills: SavedDataType
  ) => void;
  sendNodeSelectDataToClient: (e: NodeSelectEvent) => void;
};

interface State {
  skills: SavedDataType;
  skillCount: number;
  selectedCount: number;
  resetId: string;
  mounting: boolean;
}

export interface ISkillContext {
  mounting: boolean;
  skills: SavedDataType;
  skillCount: number;
  selectedCount: number;
  updateSkillState: (
    key: string,
    updatedState: NodeState,
    optional?: boolean
  ) => void;
  setSkillCount: (skillCount: number) => void;
  handleNodeSelect: (key: string, state: NodeState) => void;
  incrementSelectedCount: VoidFunction;
  decrementSelectedCount: VoidFunction;
}

const SkillContext = React.createContext<ISkillContext>({
  mounting: true,
  skills: {},
  skillCount: 0,
  selectedCount: 0,
  updateSkillState: () => undefined,
  setSkillCount: () => undefined,
  handleNodeSelect: () => undefined,
  incrementSelectedCount: () => undefined,
  decrementSelectedCount: () => undefined,
});

export class SkillTreeProvider extends React.Component<Props, State> {
  static contextType = AppContext;
  static defaultProps: DefaultProps = {
    handleSave(storage, treeId, skills) {
      return storage.setItem(`skills-${treeId}`, JSON.stringify(skills));
    },
    sendNodeSelectDataToClient() {
      return null;
    },
  };

  storage: ContextStorage | null = null;

  constructor(props: Props, context: IAppContext) {
    super(props, context);

    this.state = {
      skills: {},
      skillCount: 0,
      selectedCount: 0,
      resetId: context.resetId,
      mounting: true,
    };
  }

  componentDidMount() {
    const { storage } = this.props;
    const { context } = this;

    if (storage) {
      this.storage = storage;
    } else {
      this.storage = localStorage;
    }

    const treeSkills = this.getTreeSkills();
    let selectedCount = 0;

    Object.keys(treeSkills).map(key => {
      if (treeSkills[key].nodeState === SELECTED_STATE) {
        selectedCount++;
        const action: Action = {
          type: treeSkills[key].optional
            ? 'SELECT_OPTIONAL_SKILL'
            : 'SELECT_REQUIRED_SKILL',
        };

        context.dispatch(action);
      }
    });

    this.setState({
      skills: treeSkills,
      selectedCount,
      mounting: false,
    });

    return null;
  }

  getTreeSkills = (): SavedDataType => {
    if (this.props.savedData) {
      return this.props.savedData;
    }

    const { treeId } = this.props;

    const storedItems = this.storage!.getItem(`skills-${treeId}`);

    if (storedItems === 'undefined' || storedItems === null) {
      return {};
    }

    return JSON.parse(storedItems);
  };

  componentDidUpdate() {
    if (this.context.resetId !== this.state.resetId) {
      this.resetSkills();
    }
  }

  incrementSelectedCount = (optional: boolean = false) => {
    const action: Action = {
      type: optional ? 'SELECT_OPTIONAL_SKILL' : 'SELECT_REQUIRED_SKILL',
    };

    this.setState(prevState => {
      const { selectedCount } = prevState;
      return { selectedCount: selectedCount + 1 };
    });

    this.context.dispatch(action);
  };

  decrementSelectedCount = (optional: boolean = false) => {
    const action: Action = {
      type: optional ? 'DESELECT_OPTIONAL_SKILL' : 'DESELECT_REQUIRED_SKILL',
    };

    this.setState(prevState => {
      const { selectedCount } = prevState;
      return { selectedCount: selectedCount - 1 };
    });

    this.context.dispatch(action);
  };

  resetSkills = () => {
    return this.setState(prevState => {
      const { skills } = prevState;

      const resettedSkills: SavedDataType = mapValues(
        skills,
        (skill: SkillData) => ({
          optional: skill.optional,
          nodeState: LOCKED_STATE,
        })
      );

      return {
        skills: resettedSkills,
        resetId: this.context.resetId,
      };
    });
  };

  setSkillCount = (skillCount: number) => {
    return this.setState({
      skillCount,
    });
  };

  handleNodeSelect = (key: string, state: NodeState) => {
    return this.props.sendNodeSelectDataToClient({ key, state });
  };

  updateSkillState = (
    key: string,
    updatedState: NodeState,
    optional: boolean = false
  ) => {
    const { handleSave, treeId } = this.props;

    return this.setState(prevState => {
      const updatedSkills = {
        ...prevState.skills,
        [key]: {
          optional,
          nodeState: updatedState,
        },
      };

      handleSave(this.storage!, treeId, updatedSkills);

      return {
        skills: updatedSkills,
      };
    });
  };

  render() {
    return (
      <SkillContext.Provider
        value={{
          mounting: this.state.mounting,
          skills: this.state.skills,
          skillCount: this.state.skillCount,
          selectedCount: this.state.selectedCount,
          updateSkillState: this.updateSkillState,
          setSkillCount: this.setSkillCount,
          handleNodeSelect: this.handleNodeSelect,
          incrementSelectedCount: this.incrementSelectedCount,
          decrementSelectedCount: this.decrementSelectedCount,
        }}
      >
        {this.props.children}
      </SkillContext.Provider>
    );
  }
}

export default SkillContext;
import React, { useRef, useEffect, useContext, useCallback } from 'react';
import { isEmpty } from 'lodash';
import SkillNode from './SkillNode';
import SkillEdge from './SkillEdge';
import { Skill } from '../models';
import { Nullable } from '../models/utils';
import SkillContext from '../context/SkillContext';
import { SELECTED_STATE, LOCKED_STATE, UNLOCKED_STATE } from './constants';

type Props = {
  skill: Skill;
  parentPosition: number;
  parentHasMultipleChildren: boolean;
  shouldBeUnlocked: boolean;
} & typeof SkillTreeSegment.defaultProps;

function SkillTreeSegment({
  skill,
  hasParent,
  parentHasMultipleChildren,
  parentPosition,
  shouldBeUnlocked,
}: Props) {
  const {
    mounting,
    skills,
    updateSkillState,
    decrementSelectedCount,
    incrementSelectedCount,
    handleNodeSelect,
  } = useContext(SkillContext);

  const skillNodeRef: React.MutableRefObject<Nullable<HTMLDivElement>> = useRef(
    null
  );

  const nodeState = skills[skill.id] ? skills[skill.id].nodeState : 'locked';

  useEffect(() => {
    if (mounting) return;

    if (nodeState === SELECTED_STATE && !shouldBeUnlocked) {
      decrementSelectedCount();
      return updateSkillState(skill.id, LOCKED_STATE, skill.optional);
    }

    if (nodeState === UNLOCKED_STATE && !shouldBeUnlocked) {
      return updateSkillState(skill.id, LOCKED_STATE, skill.optional);
    }

    if (!shouldBeUnlocked) {
      return;
    }

    if (nodeState === LOCKED_STATE && shouldBeUnlocked) {
      return updateSkillState(skill.id, UNLOCKED_STATE, skill.optional);
    }
  }, [nodeState, shouldBeUnlocked, mounting]);

  useEffect(() => {
    if (mounting) return;

    if (isEmpty(skills)) {
      return updateSkillState(skill.id, UNLOCKED_STATE);
    }

    return;
  }, [mounting]);

  return (
    <div
      style={{
        margin: !hasParent ? '16px 0' : '',
      }}
    >
      {hasParent && (
        <SkillEdge
          parentHasMultipleChildren={parentHasMultipleChildren}
          state={nodeState}
          childNodeRef={skillNodeRef}
          parentPosition={parentPosition}
        />
      )}
      <div ref={skillNodeRef}>
        <SkillNode
          incSkillCount={useCallback(incrementSelectedCount, [])}
          decSkillCount={useCallback(decrementSelectedCount, [])}
          updateSkillState={updateSkillState}
          skill={skill}
          nodeState={nodeState}
          handleNodeSelect={handleNodeSelect}
        />
      </div>
    </div>
  );
}

SkillTreeSegment.defaultProps = {
  hasParent: true,
};

export default SkillTreeSegment;
