import { useContext, useEffect } from 'react';
import { Skill, SkillMap } from '../../models';
import FilterContext from '../../context/FilterContext';

interface Props {
  treeId: string;
  skills: Skill[];
}

function createSkillsTreeMap(treeId: string, skills: Skill[]) {
  const skillsTreeMap: SkillMap = {};

  function addSkillToMap(currentSkill: Skill[]) {
    currentSkill.forEach(skill => {
      if (skill.children.length > 0) {
        addSkillToMap(skill.children);
      }

      skillsTreeMap[skill.title.toLowerCase()] = treeId;
    });
  }

  addSkillToMap(skills);

  return skillsTreeMap;
}

function AddToFilterIndex(props: Props) {
  const { skills, treeId } = props;
  const { addToSkillMap } = useContext(FilterContext);

  useEffect(() => {
    const skillsTreeMap = createSkillsTreeMap(treeId, skills);
    addToSkillMap(skillsTreeMap);
  }, []);

  return null;
}

export default AddToFilterIndex;
import { useEffect, useContext, useState } from 'react';
import FilterContext from '../../context/FilterContext';

interface Props {
  setVisibility: (isVisible: boolean) => void;
  isVisible: boolean;
  treeId: string;
  disabled: boolean;
}

function FilterListener({ setVisibility, isVisible, treeId, disabled }: Props) {
  const { filtersMatches } = useContext(FilterContext);
  const [hasLoaded, setLoadingState] = useState(false);

  useEffect(() => {
    if (!hasLoaded) {
      return setLoadingState(true);
    }

    if (disabled) {
      return setVisibility(false);
    }

    if (!filtersMatches) {
      if (isVisible === true) return;

      return setVisibility(true);
    }

    if (!filtersMatches.has(treeId)) {
      if (isVisible === false) return;
      return setVisibility(false);
    }

    if (isVisible === true) return;
    return setVisibility(true);
  }, [filtersMatches]);

  return null;
}

export default FilterListener;
import React, { useState, useCallback } from 'react';
import {
  Skill,
  SavedDataType,
  ContextStorage,
  NodeSelectEvent,
} from '../models';
import SkillTreeSegment from './SkillTreeSegment';
import HSeparator from './ui/HSeparator';
import VisibilityContainer from './VisibilityContainer';
import CalculateNodeCount from './CalculateNodeCount';
import { SkillTreeProvider } from '../context/SkillContext';
import styled from 'styled-components';
import SkillTreeHeader from './SkillTreeHeader';
import AddToFilterIndex from './filter/AddToFilterIndex';
import FilterListener from './filter/FilterListener';
import useMobile from '../hooks/useMobile';

export interface Props {
  treeId: string;
  data: Skill[];
  title: React.ReactNode | string;
  description?: string;
  collapsible?: boolean;
  closedByDefault?: boolean;
  savedData?: SavedDataType;
  disabled?: boolean;
  handleSave?: (
    storage: ContextStorage,
    treeId: string,
    skills: SavedDataType
  ) => void;
  handleNodeSelect?: (e: NodeSelectEvent) => void;
}

interface CollapsibleContainerProps {
  isCollapsible: boolean;
}

function SkillTree({
  data,
  title,
  description,
  closedByDefault,
  treeId,
  savedData,
  handleSave,
  handleNodeSelect,
  collapsible = false,
  disabled = false,
}: Props) {
  const isMobile = useMobile();
  const initialVisibility = closedByDefault || disabled ? false : true;
  const [isVisible, setVisibility] = useState(initialVisibility);

  const memoizedToggleVisibility = useCallback(
    function toggleVisibility() {
      if (disabled) {
        return setVisibility(false);
      }

      if (!collapsible) {
        return setVisibility(true);
      }

      return setVisibility(!isVisible);
    },
    [isVisible, disabled, collapsible]
  );

  return (
    <React.Fragment>
      <AddToFilterIndex treeId={treeId} skills={data} />
      <FilterListener
        disabled={disabled}
        isVisible={isVisible}
        setVisibility={setVisibility}
        treeId={treeId}
      />
      <SkillTreeProvider
        treeId={treeId}
        savedData={savedData}
        handleSave={handleSave}
        sendNodeSelectDataToClient={handleNodeSelect}
      >
        <CalculateNodeCount data={data} />
        <SkillTreeContainer>
          <SkillTreeHeader
            isVisible={isVisible}
            disabled={disabled}
            handleClick={memoizedToggleVisibility}
            collapsible={collapsible}
            id={treeId}
            description={description}
            title={title}
          />
          <VisibilityContainer isVisible={isVisible}>
            <StyledSkillTree isCollapsible={collapsible}>
              {data.map((skill, i) => {
                const displaySeparator = data.length - 1 !== i && isMobile;

                return (
                  <React.Fragment key={skill.id}>
                    <SkillTreeSegment
                      shouldBeUnlocked
                      skill={skill}
                      hasParent={false}
                      parentPosition={0}
                      parentHasMultipleChildren={false}
                    />
                    <HSeparator display={displaySeparator} />
                  </React.Fragment>
                );
              })}
            </StyledSkillTree>
          </VisibilityContainer>
        </SkillTreeContainer>
      </SkillTreeProvider>
    </React.Fragment>
  );
}

export default SkillTree;

